    {
      icon: Phone,
      title: "Telefon",
      content: "+381 63 734 7023",
      link: "tel:+381637347023"
    },
    {
      icon: Mail,
      title: "Email",
      content: "info@studioleflow.com",
      link: "mailto:info@studioleflow.com"
    },
