import { useState, useRef } from "react";
import { Upload, Loader2 } from "lucide-react";
import { useEditMode } from "@/contexts/EditModeContext";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";

interface EditableImageProps {
  page: string;
  section: string;
  contentKey: string;
  currentImageUrl?: string;
  alt: string;
  className?: string;
  containerClassName?: string;
  fallbackSrc?: string;
}

export function EditableImage({
  page,
  section,
  contentKey,
  currentImageUrl,
  alt,
  className,
  containerClassName,
  fallbackSrc = "/placeholder.png"
}: EditableImageProps) {
  const { isEditMode } = useEditMode();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [previewUrl, setPreviewUrl] = useState(currentImageUrl);

  const updateImageMutation = useMutation({
    mutationFn: async (imageUrl: string) => {
      const response = await fetch("/api/cms/content/single", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          page,
          section,
          contentKey,
          contentValue: imageUrl,
        }),
      });

      if (!response.ok) throw new Error("Failed to update image");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cms/content"] });
      toast({
        title: "Slika a≈æurirana",
        description: "Slika je uspe≈°no promenjena",
      });
    },
    onError: () => {
      toast({
        title: "Gre≈°ka",
        description: "Do≈°lo je do gre≈°ke pri a≈æuriranju slike",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith("image/")) {
      toast({
        title: "Gre≈°ka",
        description: "Izabrani fajl nije slika",
        variant: "destructive",
      });
      return;
    }

    // Validate file size (max 4MB)
    if (file.size > 4 * 1024 * 1024) {
      toast({
        title: "Gre≈°ka",
        description: "Slika je prevelika (max 4MB)",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);

    try {
      // Upload to UploadThing
      const formData = new FormData();
      formData.append("file", file);

      const uploadResponse = await fetch("/api/upload-image", {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      if (!uploadResponse.ok) throw new Error("Upload failed");

      const { url } = await uploadResponse.json();
      setPreviewUrl(url);
      await updateImageMutation.mutateAsync(url);
    } catch (error) {
      toast({
        title: "Gre≈°ka",
        description: "Do≈°lo je do gre≈°ke pri upload-u slike",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleClick = () => {
    if (isEditMode && fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  return (
    <div className={cn("relative group", containerClassName)}>
      <img
        src={previewUrl || fallbackSrc}
        alt={alt}
        draggable={false}
        onContextMenu={(e) => e.preventDefault()}
        className={cn(
          "select-none",
          className,
          isEditMode && "cursor-pointer hover:opacity-80 transition-opacity"
        )}
        onClick={handleClick}
      />

      {isEditMode && (
        <>
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            className="hidden"
            onChange={handleFileSelect}
            disabled={isUploading}
          />
          <div
            className={cn(
              "absolute inset-0 flex items-center justify-center bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity cursor-pointer rounded",
              isUploading && "opacity-100"
            )}
            onClick={handleClick}
          >
            {isUploading ? (
              <Loader2 className="w-8 h-8 text-white animate-spin" />
            ) : (
              <div className="flex flex-col items-center gap-2 text-white">
                <Upload className="w-8 h-8" />
                <span className="text-sm font-medium">Promeni Sliku</span>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

