client/src/components/VerificationModal..content:
import { useState } from "react";
import { Mail, RefreshCw } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface VerificationModalProps {
  isOpen: boolean;
  onClose: () => void;
  userId: number;
  email: string;
  onSuccess: () => void;
}

export function VerificationModal({ 
  isOpen, 
  onClose, 
  userId, 
  email,
  onSuccess 
}: VerificationModalProps) {
  const { toast } = useToast();
  const [verificationCode, setVerificationCode] = useState("");
  const [isVerifying, setIsVerifying] = useState(false);
  const [isResending, setIsResending] = useState(false);

  const handleVerify = async (e: React.FormEvent) => {
    e.preventDefault();

    if (verificationCode.length !== 6) {
      toast({
        title: "Gre≈°ka",
        description: "Verifikacioni kod mora imati 6 cifara",
        variant: "destructive",
      });
      return;
    }

    setIsVerifying(true);
    try {
      const res = await apiRequest("POST", "/api/verify-email", { 
        userId, 
        code: verificationCode 
      });
      const data = await res.json();

      queryClient.setQueryData(["/api/user"], data.user);

      toast({
        title: "Uspe≈°no!",
        description: "Va≈° email je verifikovan. Dobrodo≈°li u Studio LeFlow!",
      });

      onSuccess();
    } catch (error: any) {
      toast({
        title: "Gre≈°ka",
        description: error.message || "Neva≈æeƒái verifikacioni kod",
        variant: "destructive",
      });
    } finally {
      setIsVerifying(false);
    }
  };

  const handleResend = async () => {
    setIsResending(true);
    try {
      await apiRequest("POST", "/api/resend-verification", { email });
      toast({
        title: "Uspe≈°no!",
        description: "Novi verifikacioni kod je poslat na Va≈° email",
      });
      setVerificationCode("");
    } catch (error: any) {
      toast({
        title: "Gre≈°ka",
        description: error.message || "Gre≈°ka pri slanju koda",
        variant: "destructive",
      });
    } finally {
      setIsResending(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader className="space-y-3">
          <div className="flex justify-center">
            <div className="bg-primary/10 p-3 rounded-full">
              <Mail className="w-8 h-8 text-primary" />
            </div>
          </div>
          <DialogTitle className="text-center text-2xl">
            Verifikujte Email
          </DialogTitle>
          <DialogDescription className="text-center">
            Poslali smo 6-cifreni kod na:<br />
            <span className="font-semibold text-foreground">{email}</span>
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleVerify} className="space-y-4 pt-2">
          <div className="space-y-2">
            <label className="text-sm font-medium">Verifikacioni Kod</label>
            <Input
              type="text"
              placeholder="123456"
              value={verificationCode}
              onChange={(e) => setVerificationCode(e.target.value.replace(/\D/g, "").slice(0, 6))}
              maxLength={6}
              className="text-center text-2xl tracking-widest font-bold"
              autoFocus
            />
            <p className="text-xs text-muted-foreground text-center">
              Proverite inbox i spam folder
            </p>
          </div>

          <Button 
            type="submit" 
            className="w-full" 
            disabled={isVerifying || verificationCode.length !== 6}
          >
            {isVerifying ? "Verifikujem..." : "Potvrdi Email"}
          </Button>
        </form>

        <div className="pt-4 border-t space-y-2">
          <p className="text-sm text-muted-foreground text-center">
            Niste dobili kod?
          </p>
          <Button
            variant="outline"
            onClick={handleResend}
            disabled={isResending}
            className="w-full"
          >
            <RefreshCw className={`w-4 h-4 mr-2 ${isResending ? "animate-spin" : ""}`} />
            {isResending ? "≈†aljem..." : "Po≈°alji Novi Kod"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

vite.config.ts.content:
import { defineConfig, createLogger } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import { fileURLToPath } from "url";
import runtimeErrorModal from "@replit/vite-plugin-runtime-error-modal";
import tailwindcss from "tailwindcss";
import autoprefixer from "autoprefixer";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const logger = createLogger();
const originalWarning = logger.warn;
logger.warn = (msg, options) => {
  if (msg.includes('did not pass the `from` option')) return;
  originalWarning(msg, options);
};

export default defineConfig({
  customLogger: logger,
  plugins: [
    react(),
    runtimeErrorModal(),
  ],
  css: {
    postcss: {
      plugins: [
        tailwindcss(),
        autoprefixer(),
      ],
    },
  },
  resolve: {
    alias: {
      "@": path.resolve(__dirname, "client", "src"),
      "@shared": path.resolve(__dirname, "shared"),
      "@assets": path.resolve(__dirname, "attached_assets"),
    },
  },
  root: path.resolve(__dirname, "client"),
  build: {
    outDir: path.resolve(__dirname, "dist", "public"),
    emptyOutDir: true,
    rollupOptions: {
      output: {
        manualChunks: {
