import { Resend } from 'resend';

// Cache credentials to avoid repeated API calls
let cachedCredentials: { apiKey: string; fromEmail: string } | null = null;

// Development mode storage for verification codes
interface VerificationCodeStorage {
  email: string;
  code: string;
  subject: string;
  timestamp: number;
}

let lastVerificationCode: VerificationCodeStorage | null = null;

// Export function to get last verification code (for debug endpoint)
export function getLastVerificationCode(): VerificationCodeStorage | null {
  return lastVerificationCode;
}

async function getCredentials() {
  // Return cached credentials if available
  if (cachedCredentials) {
    console.log('[RESEND] Using cached credentials');
    return cachedCredentials;
  }

  // Check for direct API key in environment variables first
  const directApiKey = process.env.RESEND_API_KEY;

  if (directApiKey) {
    console.log('[RESEND] Using RESEND_API_KEY from environment variables');

    // In production, RESEND_FROM_EMAIL is required to avoid silent failures
    const fromEmail = process.env.RESEND_FROM_EMAIL;

    if (!fromEmail) {
      // Allow fallback to test domain only in development
      if (process.env.NODE_ENV === 'development') {
        console.warn('[RESEND] RESEND_FROM_EMAIL not set, using test domain (development only)');
        cachedCredentials = {
          apiKey: directApiKey,
          fromEmail: 'onboarding@resend.dev'
        };
        return cachedCredentials;
      }

      // Fail fast in production
      console.error('[RESEND] RESEND_FROM_EMAIL environment variable is required in production');
      throw new Error('RESEND_FROM_EMAIL is not configured. Add it to secrets with your verified domain email (e.g., no-reply@mail.studioleflow.com)');
    }

    cachedCredentials = {
      apiKey: directApiKey,
      fromEmail: fromEmail
    };

    return cachedCredentials;
  }

  // Fallback to Replit connector
  const hostname = process.env.REPLIT_CONNECTORS_HOSTNAME;
  const xReplitToken = process.env.REPL_IDENTITY 
    ? 'repl ' + process.env.REPL_IDENTITY 
    : process.env.WEB_REPL_RENEWAL 
    ? 'depl ' + process.env.WEB_REPL_RENEWAL 
    : null;

  if (!xReplitToken) {
    console.error('[RESEND] X_REPLIT_TOKEN not found and no direct API key');
    throw new Error('Resend API key not configured');
  }

  console.log('[RESEND] Fetching connection settings from:', hostname);

  const response = await fetch(
    'https://' + hostname + '/api/v2/connection?include_secrets=true&connector_names=resend',
    {
      headers: {
        'Accept': 'application/json',
        'X_REPLIT_TOKEN': xReplitToken
      }
    }
  );

  const data = await response.json();
  const connectionSettings = data.items?.[0];

  if (!connectionSettings || (!connectionSettings.settings?.api_key)) {
    console.error('[RESEND] Connection settings invalid:', connectionSettings);
    throw new Error('Resend not connected');
  }

  if (!connectionSettings.settings.from_email) {
    console.error('[RESEND] Missing from_email in connection settings');
    throw new Error('Resend from_email not configured');
  }

  console.log('[RESEND] Successfully retrieved API key from connector');

  // Cache credentials for future use
  cachedCredentials = {
    apiKey: connectionSettings.settings.api_key, 
    fromEmail: connectionSettings.settings.from_email
  };

  return cachedCredentials;
}

export async function getResendClient() {
  try {
    const credentials = await getCredentials();
    console.log('[RESEND] Creating Resend client with fromEmail:', credentials.fromEmail);
    return {
      client: new Resend(credentials.apiKey),
      fromEmail: credentials.fromEmail
    };
  } catch (error) {
    console.error('[RESEND] Error getting Resend client:', error);
    throw error;
  }
}

// Helper function to extract verification code from HTML
function extractVerificationCode(html: string): string | null {
  // Try to extract 6-digit code from HTML
  const codeMatch = html.match(/\b(\d{6})\b/);
  return codeMatch?.[1] ?? null;
}

// Helper function for sending emails
export async function sendEmail({
  to,
  subject,
  html,
}: {
  to: string;
  subject: string;
  html: string;
}) {
  const isDevelopment = process.env.NODE_ENV === 'development';

  console.log('[RESEND] Sending email to:', to);
  console.log('[RESEND] Subject:', subject);

  const { client, fromEmail } = await getResendClient();

  console.log('[RESEND] From email:', fromEmail);

  try {
    const { data, error } = await client.emails.send({
      from: fromEmail,
      to,
      subject,
      html,
    });

    if (error) {
      // Check if this is a test mode restriction error
      const isTestModeError = error.message?.includes('only send testing emails') || 
                              error.message?.includes('verify a domain');

      if (isDevelopment && isTestModeError) {
        console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');
        console.log('üîß [RESEND] TEST MODE DETECTED - Using Development Fallback');
        console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');
        console.log('üìß Recipient:', to);
        console.log('üì® Subject:', subject);

        // Try to extract verification code
        const code = extractVerificationCode(html);
        if (code) {
          console.log('üîë VERIFICATION CODE:', code);

          // Store for debug endpoint
          lastVerificationCode = {
            email: to,
            code: code,
            subject: subject,
            timestamp: Date.now()
          };

          console.log('üí° Use GET /api/debug/verification-code to retrieve this code');
        } else {
          console.log('üìÑ Email content (first 200 chars):', html.substring(0, 200));
        }

        console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');
        console.log('‚ÑπÔ∏è  To enable real emails, verify your domain at resend.com/domains');
        console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');

        // Return mock success in development
        return {
          success: true,
          messageId: 'dev-mode-' + Date.now(),
          data: { id: 'dev-mode-' + Date.now() }
        };
      }

      // Re-throw if not a test mode error or not in development
      console.error('[RESEND] Failed to send email:', error);
      throw new Error(error.message);
    }

    console.log('[RESEND] Email sent successfully. ID:', data?.id);

    return { 
      success: true, 
      messageId: data?.id,
      data 
    };
  } catch (error: any) {
    // Catch network or unexpected errors
    console.error('[RESEND] Unexpected error sending email:', error);
    throw error;
  }
}

client/src/pages/video-spots.tsx.content:
import { Video, Plus, Trash2, Edit } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { FadeInWhenVisible } from "@/components/motion/FadeIn";
import { useEditMode } from "@/contexts/EditModeContext";
import { useToast } from "@/hooks/use-toast";
import { SEO } from "@/components/SEO";
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import type { VideoSpot } from "@shared/schema";

function extractYouTubeVideoId(url: string): string | null {
  const patterns = [
    /(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&\s]+)/,
    /youtube\.com\/embed\/([^&\s]+)/,
  ];

  for (const pattern of patterns) {
    const match = url.match(pattern);
    if (match && match[1]) return match[1];
  }

  return null;
}

interface VideoSpotFormData {
  title: string;
  description: string;
  artist: string;
  youtubeUrl: string;
}

export default function VideoSpots() {
  const { isEditMode } = useEditMode();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingSpot, setEditingSpot] = useState<VideoSpot | null>(null);
  const [formData, setFormData] = useState<VideoSpotFormData>({
    title: "",
    description: "",
    artist: "",
    youtubeUrl: "",
  });

  const { data: spots = [] } = useQuery<VideoSpot[]>({
    queryKey: ["/api/video-spots"],
    queryFn: () => fetch("/api/video-spots").then(r => r.json()),
  });

  const createMutation = useMutation({
    mutationFn: async (data: VideoSpotFormData) => {
      const response = await fetch("/api/video-spots", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Gre≈°ka pri dodavanju spota");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/video-spots"] });
      toast({
        title: "Uspe≈°no dodato",
        description: "Novi projekat je dodat",
      });
      setIsDialogOpen(false);
      setFormData({ title: "", description: "", artist: "", youtubeUrl: "" });
    },
    onError: (error: Error) => {
      toast({
        title: "Gre≈°ka",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: VideoSpotFormData }) => {
      const response = await fetch(`/api/video-spots/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Gre≈°ka pri a≈æuriranju spota");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/video-spots"] });
      toast({
        title: "Uspe≈°no a≈æurirano",
        description: "Projekat je a≈æuriran",
      });
      setIsDialogOpen(false);
      setEditingSpot(null);
      setFormData({ title: "", description: "", artist: "", youtubeUrl: "" });
    },
    onError: (error: Error) => {
      toast({
        title: "Gre≈°ka",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/video-spots/${id}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Gre≈°ka pri brisanju spota");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/video-spots"] });
      toast({
        title: "Uspe≈°no obrisano",
        description: "Projekat je obrisan",
      });
    },
    onError: () => {
      toast({
        title: "Gre≈°ka",
        description: "Do≈°lo je do gre≈°ke pri brisanju spota",
        variant: "destructive",
      });
    },
  });

  const handleOpenDialog = (spot?: VideoSpot) => {
    if (spot) {
      setEditingSpot(spot);
      setFormData({
        title: spot.title,
        description: spot.description,
        artist: spot.artist,
        youtubeUrl: spot.youtubeUrl,
      });
    } else {
      setEditingSpot(null);
      setFormData({ title: "", description: "", artist: "", youtubeUrl: "" });
    }
    setIsDialogOpen(true);
  };

  const handleSubmit = () => {
    if (editingSpot) {
      updateMutation.mutate({ id: editingSpot.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-background to-muted/20">
      <SEO
        title="Projekti - Studio LeFlow"
        description="Projekti u kojima je na≈° tim uƒçestvovao - video spotovi, pesme i muziƒçka produkcija. Profesionalni muziƒçki studio u Beogradu."
        keywords={["studio leflow", "leflow studio", "leflow", "muziƒçka produkcija", "beograd", "video spotovi", "produkcija pesama", "snimanje muzike", "muziƒçki projekti", "leflow studio projekti"]}
      />

      <section className="py-20">
        <div className="container px-4 mx-auto">
          <FadeInWhenVisible>
            <div className="flex flex-col items-center text-center mb-12">
              <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-primary/10 mb-4">
                <Video className="w-8 h-8 text-primary" />
              </div>
              <h1 className="text-4xl md:text-5xl font-bold mb-4">
                Na≈°i <span className="text-primary">Projekti</span>
              </h1>
              <p className="text-lg text-muted-foreground max-w-2xl">
                Projekti u kojima je na≈° tim uƒçestvovao - pogledajte ≈°ta smo kreirali
              </p>
            </div>
          </FadeInWhenVisible>

          {isEditMode && (
            <div className="flex justify-center mb-8">
              <Button
                onClick={() => handleOpenDialog()}
                className="gap-2"
                size="lg"
              >
                <Plus className="w-5 h-5" />
                Dodaj Novi Projekat
              </Button>
            </div>
          )}

          {spots.length === 0 ? (
            <FadeInWhenVisible>
              <div className="text-center py-12">
                <p className="text-muted-foreground">
                  {isEditMode
                    ? "Kliknite na dugme iznad da dodate prvi projekat"
                    : "Trenutno nema dostupnih projekata"}
                </p>
              </div>
            </FadeInWhenVisible>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-2 gap-8">
              {spots.map((spot, index) => {
                const videoId = extractYouTubeVideoId(spot.youtubeUrl);

                return (
                  <FadeInWhenVisible key={spot.id} delay={index * 0.1}>
                    <Card className="overflow-hidden hover:shadow-lg transition-shadow">
                      <CardHeader>
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <CardTitle className="text-2xl mb-2">{spot.title}</CardTitle>
                            <CardDescription className="text-base">
                              <span className="font-semibold text-primary">{spot.artist}</span>
                            </CardDescription>
                          </div>
                          {isEditMode && (
                            <div className="flex gap-2">
                              <Button
                                variant="outline"
                                size="icon"
                                onClick={() => handleOpenDialog(spot)}
                              >
                                <Edit className="w-4 h-4" />
                              </Button>
                              <Button
                                variant="destructive"
                                size="icon"
                                onClick={() => deleteMutation.mutate(spot.id)}
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </div>
                          )}
                        </div>
                      </CardHeader>
                      <CardContent className="space-y-4">
                        {videoId ? (
                          <div className="relative w-full" style={{ paddingBottom: "56.25%" }}>
                            <iframe
                              className="absolute top-0 left-0 w-full h-full rounded-lg"
                              src={`https://www.youtube-nocookie.com/embed/${videoId}`}
                              title={spot.title}
                              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                              allowFullScreen
                              loading="lazy"
                            />
                          </div>
                        ) : (
                          <div className="bg-muted rounded-lg p-8 text-center">
                            <p className="text-muted-foreground">Neva≈æeƒái YouTube URL</p>
                          </div>
                        )}

                        {spot.description && (
                          <p className="text-muted-foreground">{spot.description}</p>
                        )}
                      </CardContent>
                    </Card>
                  </FadeInWhenVisible>
                );
              })}
            </div>
          )}
        </div>
      </section>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="sm:max-w-[525px]">
          <DialogHeader>
            <DialogTitle>
              {editingSpot ? "Izmeni Projekat" : "Dodaj Novi Projekat"}
            </DialogTitle>
            <DialogDescription>
              Popunite informacije o projektu. Unesite YouTube URL videa.
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid gap-2">
              <Label htmlFor="title">Naslov</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="Unesite naslov projekta"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="artist">Izvoƒëaƒç</Label>
              <Input
                id="artist"
                value={formData.artist}
                onChange={(e) => setFormData({ ...formData, artist: e.target.value })}
                placeholder="Unesite ime izvoƒëaƒça"
              />
            </div>
            <div className="grid gap-2">
              <Label htmlFor="youtubeUrl">YouTube URL</Label>
              <Input
                id="youtubeUrl"
                value={formData.youtubeUrl}
                onChange={(e) => setFormData({ ...formData, youtubeUrl: e.target.value })}
                placeholder="https://www.youtube.com/watch?v=..."
              />
              <p className="text-xs text-muted-foreground">
                ‚ö†Ô∏è Napomena: Age-restricted YouTube videi (18+) ne mogu da se prikazuju preko iframe-a. Koristite samo javne videe bez starosnih ograniƒçenja.
              </p>
            </div>
            <div className="grid gap-2">
              <Label htmlFor="description">Opis</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Unesite opis projekta (opciono)"
                rows={3}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsDialogOpen(false)}>
              Otka≈æi
            </Button>
            <Button onClick={handleSubmit}>
              {editingSpot ? "Saƒçuvaj Izmene" : "Dodaj Projekat"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

