// Blueprint reference: blueprint:javascript_auth_all_persistance
import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import { Express } from "express";
import session from "express-session";
import { scrypt, randomBytes, timingSafeEqual } from "crypto";
import { promisify } from "util";
import { storage } from "./storage";
import { User as SelectUser } from "@shared/schema";
import { sendEmail } from "./resend-client";

declare global {
  namespace Express {
    interface User extends SelectUser {}
  }
}

const scryptAsync = promisify(scrypt);

export async function hashPassword(password: string) {
  const salt = randomBytes(16).toString("hex");
  const buf = (await scryptAsync(password, salt, 64)) as Buffer;
  return `${buf.toString("hex")}.${salt}`;
}

export async function comparePasswords(supplied: string, stored: string) {
  const [hashed, salt] = stored.split(".");
  if (!hashed || !salt) {
    throw new Error("Invalid password format");
  }
  const hashedBuf = Buffer.from(hashed, "hex");
  const suppliedBuf = (await scryptAsync(supplied, salt, 64)) as Buffer;
  return timingSafeEqual(hashedBuf, suppliedBuf);
}

function generateVerificationCode(): string {
  return Math.floor(100000 + Math.random() * 900000).toString();
}

export function setupAuth(app: Express) {
  const sessionSettings: session.SessionOptions = {
    secret: process.env.SESSION_SECRET!,
    resave: false,
    saveUninitialized: false,
    store: storage.sessionStore,
  };

  app.set("trust proxy", 1);
  app.use(session(sessionSettings));
  app.use(passport.initialize());
  app.use(passport.session());

  passport.use(
    new LocalStrategy(async (username, password, done) => {
      try {
        console.log('[AUTH] Login attempt for username/email:', username);

        // Try to find user by username first, then by email
        let user = await storage.getUserByUsername(username);

        if (!user) {
          // Try by email if username not found
          user = await storage.getUserByEmail(username);
        }

        if (!user) {
          console.log('[AUTH] User not found:', username);
          return done(null, false, { message: "Pogre≈°no korisniƒçko ime ili lozinka" });
        }

        const passwordMatch = await comparePasswords(password, user.password);
        console.log('[AUTH] Password match:', passwordMatch);

        if (!passwordMatch) {
          console.log('[AUTH] Password mismatch for user:', username);
          return done(null, false, { message: "Pogre≈°no korisniƒçko ime ili lozinka" });
        }

        // Check if user is banned
        if (user.banned) {
          console.log('[AUTH] User is banned:', username);
          return done(null, false, { message: "Va≈° nalog je banovan" });
        }

        console.log('[AUTH] Login successful for user:', user.username);
        return done(null, user);
      } catch (error) {
        console.error('[AUTH] Login error:', error);
        return done(error);
      }
    }),
  );

  passport.serializeUser((user, done) => done(null, user.id));
  passport.deserializeUser(async (id: number, done) => {
    const user = await storage.getUser(id);
    done(null, user);
  });

  app.post("/api/register", async (req, res, next) => {
    try {
      // Validate with Zod schema from shared/schema.ts
      const { insertUserSchema } = await import("@shared/schema");
      const validatedData = insertUserSchema.parse(req.body);

      // Normalize email and username to lowercase for consistency
      const normalizedEmail = validatedData.email.toLowerCase();
      const normalizedUsername = validatedData.username.toLowerCase();

      const existingUser = await storage.getUserByUsername(normalizedUsername);
      if (existingUser) {
        return res.status(400).send("Korisniƒçko ime veƒá postoji");
      }

      const existingEmail = await storage.getUserByEmail(normalizedEmail);
      if (existingEmail) {
        return res.status(400).send("Email adresa veƒá postoji");
      }

      const user = await storage.createUser({
        ...validatedData,
        email: normalizedEmail,
        username: normalizedUsername,
        password: await hashPassword(validatedData.password),
      });

      // Generate verification code and save it
      const verificationCode = generateVerificationCode();
      await storage.setVerificationCode(user.id, verificationCode);

      console.log(`[AUTH] Attempting to send verification email to: ${validatedData.email}`);
      console.log(`[AUTH] Verification code generated: ${verificationCode}`)

      try {
        const result = await sendEmail({
          to: validatedData.email,
          subject: 'Potvrdite Va≈°u Email Adresu - Studio LeFlow',
          html: `
            <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
              <h2 style="color: #7c3aed;">Studio LeFlow</h2>
              <h3>Dobrodo≈°li u Studio LeFlow zajednicu!</h3>
              <p>Hvala ≈°to ste se registrovali. Da biste zavr≈°ili registraciju, unesite sledeƒái verifikacioni kod:</p>
              <div style="background-color: #f3f4f6; padding: 20px; text-align: center; margin: 30px 0; border-radius: 8px;">
                <h1 style="color: #7c3aed; font-size: 36px; letter-spacing: 8px; margin: 0;">${verificationCode}</h1>
              </div>
              <p>Ovaj kod istiƒçe za 15 minuta.</p>
              <p>Ako niste kreirali nalog, ignori≈°ite ovaj email.</p>
              <hr style="margin: 30px 0; border: none; border-top: 1px solid #e5e7eb;">
              <p style="color: #666; font-size: 12px;">Studio LeFlow - Profesionalna Muziƒçka Produkcija</p>
            </div>
          `
        });
        console.log(`[AUTH] Verification email sent successfully to ${validatedData.email}. Message ID: ${result.messageId}`);
      } catch (emailError: any) {
        console.error("[AUTH] Failed to send verification email:", emailError);
        console.error("[AUTH] Email error details:", emailError.message);

        // Delete the user since we couldn't send the verification email
        await storage.deleteUser(user.id);

        return res.status(500).json({ 
          error: "Gre≈°ka pri slanju verifikacionog email-a. Molimo proverite da li je email adresa ispravna i poku≈°ajte ponovo." 
        });
      }

      // Don't log the user in yet - they need to verify email first
      // SECURITY: Don't expose password hash or verification code
      const { password, verificationCode: _, ...userWithoutSensitiveData } = user;
      res.status(201).json(userWithoutSensitiveData);
    } catch (error: any) {
      if (error.name === "ZodError") {
        return res.status(400).json({ error: "Validacija nije uspela", details: error.errors });
      }
      res.status(500).send(error.message);
    }
  });

  app.post("/api/login", passport.authenticate("local"), (req, res) => {
    // SECURITY: Don't expose password hash
    const { password, ...userWithoutPassword } = req.user!;
    res.status(200).json(userWithoutPassword);
  });

  app.post("/api/logout", (req, res, next) => {
    req.logout((err) => {
      if (err) return next(err);
      res.sendStatus(200);
    });
  });

  app.get("/api/user", (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    // SECURITY: Don't expose password hash
    const { password, ...userWithoutPassword } = req.user!;
    res.json(userWithoutPassword);
  });

  app.post("/api/forgot-password", async (req, res) => {
    try {
      const { email } = req.body;

      if (!email) {
        return res.status(400).json({ error: "Email je obavezan" });
      }

      const user = await storage.getUserByEmail(email);

      // Always return success to prevent user enumeration
      if (!user) {
        console.log(`[AUTH] Password reset requested for non-existent email: ${email}`);
        return res.json({ success: true, message: "Ako email postoji, kod za reset lozinke je poslat" });
      }

      // Generate 6-digit reset code
      const resetToken = generateVerificationCode();
      await storage.setPasswordResetToken(user.id, resetToken);

      console.log(`[AUTH] Password reset requested for: ${email}`);
      console.log(`[AUTH] Reset code generated: ${resetToken}`);

      try {
        await sendEmail({
          to: email,
          subject: 'Resetovanje Lozinke - Studio LeFlow',
          html: `
            <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
              <h2 style="color: #7c3aed;">Studio LeFlow</h2>
              <h3>Zahtev za Resetovanje Lozinke</h3>
              <p>Primili smo zahtev za resetovanje va≈°e lozinke. Unesite sledeƒái kod da biste kreirali novu lozinku:</p>
              <div style="background-color: #f3f4f6; padding: 20px; text-align: center; margin: 30px 0; border-radius: 8px;">
                <h1 style="color: #7c3aed; font-size: 36px; letter-spacing: 8px; margin: 0;">${resetToken}</h1>
              </div>
              <p>Ovaj kod istiƒçe za 15 minuta.</p>
              <p>Ako niste zatra≈æili resetovanje lozinke, ignori≈°ite ovaj email. Va≈°a lozinka neƒáe biti promenjena.</p>
              <hr style="margin: 30px 0; border: none; border-top: 1px solid #e5e7eb;">
              <p style="color: #666; font-size: 12px;">Studio LeFlow - Profesionalna Muziƒçka Produkcija</p>
            </div>
          `
        });
        console.log(`[AUTH] Password reset email sent successfully to ${email}`);
      } catch (emailError: any) {
        console.error("[AUTH] Failed to send password reset email:", emailError);
        // Don't reveal if email exists - return generic error
        return res.status(500).json({ 
          error: "Gre≈°ka pri slanju email-a. Molimo poku≈°ajte ponovo." 
        });
      }

      // Don't reveal user ID or any identifying information
      res.json({ success: true, message: "Ako email postoji, kod za reset lozinke je poslat" });
    } catch (error: any) {
      console.error("[AUTH] Forgot password error:", error);
      res.status(500).json({ error: "Gre≈°ka na serveru" });
    }
  });

  app.post("/api/reset-password", async (req, res) => {
    try {
      const { email, token, newPassword } = req.body;

      if (!email || !token || !newPassword) {
        return res.status(400).json({ error: "Svi parametri su obavezni" });
      }

      // Validate new password
      if (newPassword.length < 8) {
        return res.status(400).json({ error: "Lozinka mora imati najmanje 8 karaktera" });
      }

      // Find user by email
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(400).json({ error: "Neva≈æeƒái ili istekao kod za reset lozinke" });
      }

      // Verify reset token
      const isValid = await storage.verifyPasswordResetToken(user.id, token);
      if (!isValid) {
        return res.status(400).json({ error: "Neva≈æeƒái ili istekao kod za reset lozinke" });
      }

      // Hash new password and update
      const hashedPassword = await hashPassword(newPassword);
      await storage.updatePassword(user.id, hashedPassword);
      await storage.clearPasswordResetToken(user.id);

      console.log(`[AUTH] Password successfully reset for user: ${email}`);

      res.json({ success: true, message: "Lozinka je uspe≈°no promenjena" });
    } catch (error: any) {
      console.error("[AUTH] Reset password error:", error);
      res.status(500).json({ error: "Gre≈°ka na serveru" });
    }
  });
}

