function NewsletterTab() {
  const { toast } = useToast();
  const [copiedEmail, setCopiedEmail] = useState<string | null>(null);
  const [subject, setSubject] = useState("");
  const [htmlContent, setHtmlContent] = useState("");
  const [isSending, setIsSending] = useState(false);

  const { data: subscribers = [], isLoading: subscribersLoading } = useQuery({
    queryKey: ["/api/newsletter/subscribers"],
    queryFn: async () => {
      const response = await fetch("/api/newsletter/subscribers");
      if (!response.ok) throw new Error("Failed to load subscribers");
      return await response.json();
    },
  });

  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/newsletter/stats"],
    queryFn: async () => {
      const response = await fetch("/api/newsletter/stats");
      if (!response.ok) throw new Error("Failed to load stats");
      return await response.json();
    },
  });

  const deleteSubscriberMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/newsletter/subscribers/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Failed to delete subscriber");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/newsletter/subscribers"] });
      queryClient.invalidateQueries({ queryKey: ["/api/newsletter/stats"] });
      toast({
        title: "Uspeh",
        description: "Pretplatnik je uspe≈°no uklonjen",
      });
    },
    onError: () => {
      toast({
        title: "Gre≈°ka",
        description: "Do≈°lo je do gre≈°ke pri brisanju pretplatnika",
        variant: "destructive",
      });
    },
  });

  const handleSendNewsletter = async () => {
    if (!subject.trim() || !htmlContent.trim()) {
      toast({
        title: "Gre≈°ka",
        description: "Subject i sadr≈æaj su obavezni",
        variant: "destructive",
      });
      return;
    }

    setIsSending(true);
    try {
      const response = await fetch("/api/newsletter/send", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ subject, htmlContent }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Failed to send newsletter");
      }

      const result = await response.json();
      toast({
        title: "Uspeh",
        description: result.message,
      });
      setSubject("");
      setHtmlContent("");
    } catch (error: any) {
      toast({
        title: "Gre≈°ka",
        description: error.message || "Do≈°lo je do gre≈°ke pri slanju newslettera",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  const handleCopyEmail = (email: string) => {
    navigator.clipboard.writeText(email);
    setCopiedEmail(email);
    toast({
      title: "Kopirano!",
      description: `Email ${email} je kopiran u clipboard`,
    });
    setTimeout(() => setCopiedEmail(null), 2000);
  };

  const handleCopyAll = () => {
    const confirmedEmails = subscribers
      .filter((s: any) => s.status === 'confirmed')
      .map((s: any) => s.email)
      .join(', ');
    navigator.clipboard.writeText(confirmedEmails);
    toast({
      title: "Kopirano!",
      description: `${subscribers.filter((s: any) => s.status === 'confirmed').length} email adresa kopirano`,
    });
  };

  const formatDate = (dateString: string | null) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString("sr-RS", {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "confirmed":
        return <Badge className="bg-green-500">Potvrƒëeno</Badge>;
      case "pending":
        return <Badge variant="outline">Na ƒçekanju</Badge>;
      case "unsubscribed":
        return <Badge variant="destructive">Odjavljeno</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium">Ukupno Pretplatnika</CardTitle>
          </CardHeader>
          <CardContent>
            {statsLoading ? (
              <Skeleton className="h-8 w-16" />
            ) : (
              <div className="text-3xl font-bold">{stats?.total || 0}</div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium">Potvrƒëeno</CardTitle>
          </CardHeader>
          <CardContent>
            {statsLoading ? (
              <Skeleton className="h-8 w-16" />
            ) : (
              <div className="text-3xl font-bold text-green-600">{stats?.confirmed || 0}</div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium">Na ƒçekanju</CardTitle>
          </CardHeader>
          <CardContent>
            {statsLoading ? (
              <Skeleton className="h-8 w-16" />
            ) : (
              <div className="text-3xl font-bold text-orange-600">{stats?.pending || 0}</div>
            )}
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center gap-2">
            <Mail className="w-5 h-5 text-primary" />
            <div>
              <CardTitle>Po≈°alji Newsletter</CardTitle>
              <CardDescription>Kreirajte i po≈°aljite email kampanju svim potvrƒëenim pretplatnicima</CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <Label htmlFor="newsletter-subject">Subject</Label>
            <Input
              id="newsletter-subject"
              placeholder="Naslov newsletter poruke..."
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              className="mt-2"
            />
          </div>
          <div>
            <Label htmlFor="newsletter-content">Sadr≈æaj</Label>
            <div className="mt-2">
              <RichTextEditor
                content={htmlContent}
                onChange={setHtmlContent}
                placeholder="Unesite sadr≈æaj newsletter poruke..."
              />
            </div>
          </div>
          <div className="flex items-center justify-between pt-4">
            <p className="text-sm text-muted-foreground">
              Newsletter ƒáe biti poslat na {stats?.confirmed || 0} potvrƒëenih email adresa
            </p>
            <Button 
              onClick={handleSendNewsletter} 
              disabled={isSending || !subject.trim() || !htmlContent.trim()}
              className="gap-2"
            >
              <Send className="w-4 h-4" />
              {isSending ? "Slanje..." : "Po≈°alji Newsletter"}
            </Button>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Pretplatnici</CardTitle>
              <CardDescription>Lista svih email pretplatnika</CardDescription>
            </div>
            <Button onClick={handleCopyAll} variant="outline" size="sm">
              Kopiraj sve potvrƒëene email adrese
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {subscribersLoading ? (
            <div className="space-y-2">
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          ) : subscribers.length === 0 ? (
            <p className="text-center text-muted-foreground py-8">
              Nema pretplatnika
            </p>
          ) : (
            <div className="rounded-md border">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Email</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Datum prijave</TableHead>
                    <TableHead>Datum potvrde</TableHead>
                    <TableHead>Akcije</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {subscribers.map((subscriber: any) => (
                    <TableRow key={subscriber.id}>
                      <TableCell className="font-medium">{subscriber.email}</TableCell>
                      <TableCell>{getStatusBadge(subscriber.status)}</TableCell>
                      <TableCell>{formatDate(subscriber.subscribedAt)}</TableCell>
                      <TableCell>{formatDate(subscriber.confirmedAt)}</TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleCopyEmail(subscriber.email)}
                          >
                            {copiedEmail === subscriber.email ? "Kopirano!" : "Kopiraj"}
                          </Button>
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                className="text-destructive hover:text-destructive"
                              >
                                <Trash2 className="h-4 w-4" />
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Ukloni pretplatnika</AlertDialogTitle>
                                <AlertDialogDescription>
                                  Da li ste sigurni da ≈æelite da uklonite <strong>{subscriber.email}</strong> sa newsletter liste?
                                  Ova akcija se ne mo≈æe poni≈°titi.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Otka≈æi</AlertDialogCancel>
                                <AlertDialogAction
                                  onClick={() => deleteSubscriberMutation.mutate(subscriber.id)}
                                  className="bg-destructive hover:bg-destructive/90"
                                >
                                  Ukloni
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

function MessagesTab() {
  const { toast } = useToast();
  const [selectedConversation, setSelectedConversation] = useState<ConversationWithUsers | null>(null);

  const { data: conversations = [], isLoading: conversationsLoading } = useQuery<ConversationWithUsers[]>({
    queryKey: ["/api/admin/messages/conversations"],
