import { useEffect } from 'react';

interface SEOProps {
  title?: string;
  description?: string;
  keywords?: string[];
  ogImage?: string;
  ogType?: string;
  structuredData?: object;
}

// Get absolute URL for Open Graph images
function getAbsoluteUrl(path: string): string {
  // In production, use the actual domain
  if (typeof window !== 'undefined') {
    const protocol = window.location.protocol;
    const host = window.location.host;
    return `${protocol}//${host}${path}`;
  }
  return path;
}

export function SEO({
  title = "Studio LeFlow - Profesionalni Muziƒçki Studio u Beogradu | Snimanje, Miks, Mastering",
  description = "Vrhunski muziƒçki studio u Beogradu. Snimanje, mix/mastering, instrumentali, video spotovi. WA-47, Apollo Twin X, UAD plugins. Preko 5 godina iskustva.",
  keywords = [
    "studio leflow",
    "leflow studio",
    "leflow",
    "leflow beograd",
    "studio leflow beograd",
    "leflow studio beograd",
    "muziƒçki studio beograd",
    "muzicki studio beograd",
    "snimanje pesme beograd",
    "snimanje vokala beograd",
    "miks i mastering",
    "mix mastering beograd",
    "voice over studio",
    "podcast studio beograd",
    "muziƒçka produkcija",
    "audio produkcija beograd",
    "mastering beograd",
    "producent muzike beograd",
    "beatmaker beograd",
    "recording studio belgrade",
    "leflow music studio",
    "najbolji muziƒçki studio beograd"
  ],
  ogImage = "/public/og-image.jpg",
  ogType = "website",
  structuredData,
}: SEOProps) {
  useEffect(() => {
    // Set document title
    document.title = title;

    // Set/update meta tags
    const setMetaTag = (name: string, content: string, property = false) => {
      const attr = property ? 'property' : 'name';
      let element = document.querySelector(`meta[${attr}="${name}"]`);
      if (!element) {
        element = document.createElement('meta');
        element.setAttribute(attr, name);
        document.head.appendChild(element);
      }
      element.setAttribute('content', content);
    };

    // Standard meta tags
    setMetaTag('description', description);
    setMetaTag('keywords', keywords.join(', '));

    // Get absolute URL for Open Graph image
    const absoluteOgImage = getAbsoluteUrl(ogImage);

    // Open Graph tags
    setMetaTag('og:title', title, true);
    setMetaTag('og:description', description, true);
    setMetaTag('og:image', absoluteOgImage, true);
    setMetaTag('og:image:width', '1200', true);
    setMetaTag('og:image:height', '630', true);
    setMetaTag('og:type', ogType, true);
    setMetaTag('og:site_name', 'Studio LeFlow', true);
    setMetaTag('og:locale', 'sr_RS', true);
    setMetaTag('og:url', window.location.href, true);

    // Twitter cards
    setMetaTag('twitter:card', 'summary_large_image');
    setMetaTag('twitter:title', title);
    setMetaTag('twitter:description', description);
    setMetaTag('twitter:image', absoluteOgImage);

    // Structured data
    if (structuredData) {
      let script = document.querySelector('script[type="application/ld+json"]');
      if (!script) {
        script = document.createElement('script');
        script.setAttribute('type', 'application/ld+json');
        document.head.appendChild(script);
      }
      script.textContent = JSON.stringify(structuredData);
    }
  }, [title, description, keywords, ogImage, ogType, structuredData]);

  return <></>;
}

