import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0" +
  " hover-elevate active-elevate-2",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border border-primary-border",
        destructive:
          "bg-destructive text-destructive-foreground border border-destructive-border",
        outline:
          // Shows the background color of whatever card / sidebar / accent background it is inside of.
          // Inherits the current text color.
          " border [border-color:var(--button-outline)]  shadow-xs active:shadow-none ",
        secondary: "border bg-secondary text-secondary-foreground border border-secondary-border ",
        // Add a transparent border so that when someone toggles a border on later, it doesn't shift layout/size.
        ghost: "border border-transparent",
      },
      // Heights are set as "min" heights, because sometimes Ai will place large amount of content
      // inside buttons. With a min-height they will look appropriate with small amounts of content,
      // but will expand to fit large amounts of content.
      size: {
        default: "min-h-9 px-4 py-2",
        sm: "min-h-8 rounded-md px-3 text-xs",
        lg: "min-h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

client/src/components/cms/EditableText.t.content:
import { useState, useRef, useEffect } from "react";
import { useEditMode } from "@/contexts/EditModeContext";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Edit2, Check, X } from "lucide-react";
import { Button } from "@/components/ui/button";

interface EditableTextProps {
  page: string;
  section: string;
  contentKey: string;
  value: string;
  className?: string;
  as?: "h1" | "h2" | "h3" | "h4" | "h5" | "h6" | "p" | "span";
  multiline?: boolean;
  placeholder?: string;
}

export function EditableText({
  page,
  section,
  contentKey,
  value,
  className = "",
  as: Tag = "p",
  multiline = false,
  placeholder = "Kliknite da izmenite tekst..."
}: EditableTextProps) {
  const { isEditMode } = useEditMode();
  const [isEditing, setIsEditing] = useState(false);
  const [editValue, setEditValue] = useState(value);
  const inputRef = useRef<HTMLInputElement | HTMLTextAreaElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    setEditValue(value);
  }, [value]);

  useEffect(() => {
    if (isEditing && inputRef.current) {
      inputRef.current.focus();
      inputRef.current.select();
    }
  }, [isEditing]);

  const updateMutation = useMutation({
    mutationFn: async (newValue: string) => {
      const response = await fetch("/api/cms/content/single", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          page,
          section,
          contentKey,
          contentValue: newValue,
        }),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Gre≈°ka pri ƒçuvanju izmena");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/cms/content`] });
      toast({
        title: "Saƒçuvano",
        description: "Izmene su uspe≈°no saƒçuvane",
      });
      setIsEditing(false);
    },
    onError: () => {
      toast({
        title: "Gre≈°ka",
        description: "Do≈°lo je do gre≈°ke pri ƒçuvanju izmena",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    if (editValue.trim() === "") {
      toast({
        title: "Gre≈°ka",
        description: "Tekst ne mo≈æe biti prazan",
        variant: "destructive",
      });
      return;
    }
    updateMutation.mutate(editValue);
  };

  const handleCancel = () => {
    setEditValue(value);
    setIsEditing(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !multiline) {
      e.preventDefault();
      handleSave();
    } else if (e.key === "Escape") {
      handleCancel();
    }
  };

  if (!isEditMode) {
    return <Tag className={className}>{value}</Tag>;
  }

  if (isEditing) {
    return (
      <div className="relative group">
        {multiline ? (
          <textarea
            ref={inputRef as React.RefObject<HTMLTextAreaElement>}
            value={editValue}
            onChange={(e) => setEditValue(e.target.value)}
            onKeyDown={handleKeyDown}
            className={`${className} w-full border-2 border-primary rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-primary min-h-[100px]`}
            placeholder={placeholder}
          />
        ) : (
          <input
            ref={inputRef as React.RefObject<HTMLInputElement>}
            type="text"
            value={editValue}
            onChange={(e) => setEditValue(e.target.value)}
            onKeyDown={handleKeyDown}
            className={`${className} w-full border-2 border-primary rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-primary`}
            placeholder={placeholder}
          />
        )}
        <div className="flex gap-2 mt-2">
          <Button
            size="sm"
            onClick={handleSave}
            disabled={updateMutation.isPending}
            className="gap-1"
          >
            <Check className="w-4 h-4" />
            Saƒçuvaj
          </Button>
          <Button
            size="sm"
            variant="outline"
            onClick={handleCancel}
            disabled={updateMutation.isPending}
            className="gap-1"
          >
            <X className="w-4 h-4" />
            Otka≈æi
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div
      className="relative group cursor-pointer hover:bg-primary/5 rounded-lg transition-colors p-2 -m-2"
      onClick={() => setIsEditing(true)}
    >
      <Tag className={className}>{value || placeholder}</Tag>
      <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity">
        <div className="bg-primary text-primary-foreground rounded-md p-1">
          <Edit2 className="w-3 h-3" />
        </div>
      </div>
    </div>
  );
}

