import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { Mail, Loader2 } from "lucide-react";
import { motion } from "framer-motion";

interface NewsletterFormProps {
  variant?: "default" | "footer";
  className?: string;
}

export function NewsletterForm({ variant = "default", className = "" }: NewsletterFormProps) {
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email || !email.includes("@")) {
      toast({
        title: "Gre≈°ka",
        description: "Unesite validnu email adresu",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await fetch("/api/newsletter/subscribe", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Uspe≈°no!",
          description: data.message || "Proverite email za link za potvrdu",
        });
        setEmail("");
      } else {
        toast({
          title: "Gre≈°ka",
          description: data.error || "Do≈°lo je do gre≈°ke",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error("Newsletter subscribe error:", error);
      toast({
        title: "Gre≈°ka",
        description: "Gre≈°ka na serveru. Poku≈°ajte ponovo.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (variant === "footer") {
    return (
      <form onSubmit={handleSubmit} className={`flex gap-2 ${className}`}>
        <Input
          type="email"
          placeholder="Va≈°a email adresa"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          disabled={isLoading}
          className="flex-1"
        />
        <Button type="submit" disabled={isLoading} size="icon">
          {isLoading ? (
            <Loader2 className="h-4 w-4 animate-spin" />
          ) : (
            <Mail className="h-4 w-4" />
          )}
        </Button>
      </form>
    );
  }

  return (
    <motion.form
      onSubmit={handleSubmit}
      className={`max-w-md mx-auto ${className}`}
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.2 }}
    >
      <div className="flex gap-2">
        <Input
          type="email"
          placeholder="Unesite va≈°u email adresu"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          disabled={isLoading}
          className="flex-1"
        />
        <Button type="submit" disabled={isLoading}>
          {isLoading ? (
            <Loader2 className="h-4 w-4 animate-spin" />
          ) : (
            "Prijavi se"
          )}
        </Button>
      </div>
      <p className="text-xs text-muted-foreground text-center mt-2">
        Prijavom na newsletter prihvatate na≈°u politiku privatnosti
      </p>
    </motion.form>
  );
}

scripts/generate-favicons.ts.content:
import sharp from 'sharp';
import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';

const sourceImage = 'attached_assets/icon_1762539841884.png';
const outputDir = 'client/public';

const sizes = [
  { name: 'favicon-16x16.png', size: 16 },
  { name: 'favicon-32x32.png', size: 32 },
  { name: 'favicon.png', size: 48 }, // Default favicon
  { name: 'favicon-192x192.png', size: 192 }, // Android
  { name: 'favicon-512x512.png', size: 512 }, // Google Search recommended
  { name: 'apple-touch-icon.png', size: 180 }, // iOS
];

async function generateFavicons() {
  console.log('üé® Generating favicons...');

  for (const { name, size } of sizes) {
    try {
      await sharp(sourceImage)
        .resize(size, size, {
          fit: 'contain',
          background: { r: 0, g: 0, b: 0, alpha: 0 } // Transparent background
        })
        .png()
        .toFile(join(outputDir, name));

      console.log(`‚úÖ Generated ${name} (${size}x${size})`);
    } catch (error) {
      console.error(`‚ùå Failed to generate ${name}:`, error);
    }
  }

  // Generate ICO file for older browsers (32x32)
  try {
    await sharp(sourceImage)
      .resize(32, 32, {
        fit: 'contain',
        background: { r: 0, g: 0, b: 0, alpha: 0 }
      })
      .toFile(join(outputDir, 'favicon.ico'));

    console.log('‚úÖ Generated favicon.ico (32x32)');
  } catch (error) {
    console.error('‚ùå Failed to generate favicon.ico:', error);
  }

  console.log('üéâ All favicons generated successfully!');
}

generateFavicons().catch(console.error);

