/**
 * End-to-End Messaging System Test
 * Tests: Authentication, User Search, Message Send/Receive, Read Receipts, WebSocket Events
 */

import WebSocket from 'ws';

const BASE_URL = 'http://localhost:5000';
let sessionCookie = '';

interface ApiResponse {
  ok: boolean;
  status: number;
  data: any;
}

// Helper: Make authenticated HTTP request
async function request(method: string, path: string, body?: any): Promise<ApiResponse> {
  const headers: Record<string, string> = {
    'Content-Type': 'application/json',
  };

  if (sessionCookie) {
    headers['Cookie'] = sessionCookie;
  }

  const response = await fetch(`${BASE_URL}${path}`, {
    method,
    headers,
    body: body ? JSON.stringify(body) : undefined,
  });

  const setCookie = response.headers.get('set-cookie');
  if (setCookie && !sessionCookie) {
    sessionCookie = setCookie.split(';')[0];
  }

  let data;
  const contentType = response.headers.get('content-type');
  if (contentType?.includes('application/json')) {
    data = await response.json();
  } else {
    data = await response.text();
  }

  return {
    ok: response.ok,
    status: response.status,
    data,
  };
}

// Test 1: User Authentication
async function testAuthentication() {
  console.log('\nüîê Test 1: User Authentication');

  // Login as admin (LocalStrategy expects 'username' field, not 'email')
  const loginRes = await request('POST', '/api/login', {
    username: 'admin@leflow.rs', // Can be username or email
    password: 'admin123',
  });

  if (loginRes.ok && loginRes.data.user) {
    console.log(`‚úÖ Login successful: ${loginRes.data.user.username} (ID: ${loginRes.data.user.id})`);
    console.log(`   Email verified: ${loginRes.data.user.emailVerified}`);
    return loginRes.data.user;
  } else {
    console.log(`‚ùå Login failed: ${loginRes.status} ${JSON.stringify(loginRes.data)}`);
    throw new Error('Authentication failed');
  }
}

// Test 2: User Search
async function testUserSearch(query: string) {
  console.log(`\nüîç Test 2: User Search (query: "${query}")`);

  const searchRes = await request('GET', `/api/users/search?q=${query}`);

  if (searchRes.ok && Array.isArray(searchRes.data)) {
    console.log(`‚úÖ Search found ${searchRes.data.length} users:`);
    searchRes.data.forEach((user: any) => {
      console.log(`   - ${user.username} (ID: ${user.id}, verified: ${user.emailVerified})`);
    });
    return searchRes.data;
  } else {
    console.log(`‚ùå Search failed: ${searchRes.status}`);
    return [];
  }
}

// Test 3: Get Conversations
async function testGetConversations() {
  console.log('\nüí¨ Test 3: Get Conversations');

  const convRes = await request('GET', '/api/conversations');

  if (convRes.ok && Array.isArray(convRes.data)) {
    console.log(`‚úÖ Found ${convRes.data.length} conversations`);
    convRes.data.forEach((conv: any) => {
      console.log(`   - Conversation with user ${conv.otherUser?.username} (${conv.messageCount} messages)`);
    });
    return convRes.data;
  } else {
    console.log(`‚ùå Failed to get conversations: ${convRes.status}`);
    return [];
  }
}

// Test 4: Send Message
async function testSendMessage(receiverId: number, content: string) {
  console.log(`\nüì§ Test 4: Send Message to user ${receiverId}`);

  const sendRes = await request('POST', '/api/messages/send', {
    receiverId,
    content,
  });

  if (sendRes.ok && sendRes.data.message) {
    console.log(`‚úÖ Message sent successfully (ID: ${sendRes.data.message.id})`);
    console.log(`   Content: "${sendRes.data.message.content}"`);
    console.log(`   Timestamp: ${sendRes.data.message.createdAt}`);
    return sendRes.data.message;
  } else {
    console.log(`‚ùå Send message failed: ${sendRes.status} ${JSON.stringify(sendRes.data)}`);
    return null;
  }
}

// Test 5: Get Messages in Conversation
async function testGetMessages(otherUserId: number) {
  console.log(`\nüì• Test 5: Get Messages with user ${otherUserId}`);

  const msgRes = await request('GET', `/api/messages/conversation/${otherUserId}`);

  if (msgRes.ok && Array.isArray(msgRes.data)) {
    console.log(`‚úÖ Retrieved ${msgRes.data.length} messages`);
    msgRes.data.slice(-3).forEach((msg: any) => {
      console.log(`   - [${msg.createdAt}] ${msg.sender.username}: "${msg.content}"`);
    });
    return msgRes.data;
  } else {
    console.log(`‚ùå Get messages failed: ${msgRes.status}`);
    return [];
  }
}

// Test 6: Mark Messages as Read
async function testMarkAsRead(conversationUserId: number) {
  console.log(`\n‚úÖ Test 6: Mark Messages as Read (conversation with ${conversationUserId})`);

  const readRes = await request('PUT', '/api/messages/mark-read', {
    conversationUserId,
  });

  if (readRes.ok) {
    console.log(`‚úÖ Messages marked as read successfully`);
    return true;
  } else {
    console.log(`‚ùå Mark as read failed: ${readRes.status}`);
    return false;
  }
}

// Test 7: Get Unread Count
async function testUnreadCount() {
  console.log('\nüîî Test 7: Get Unread Message Count');

  const countRes = await request('GET', '/api/messages/unread-count');

  if (countRes.ok && typeof countRes.data.count === 'number') {
    console.log(`‚úÖ Unread messages: ${countRes.data.count}`);
    return countRes.data.count;
  } else {
    console.log(`‚ùå Get unread count failed: ${countRes.status}`);
    return null;
  }
}

// Test 8: WebSocket Connection
async function testWebSocket(userId: number): Promise<void> {
  console.log('\nüîå Test 8: WebSocket Real-Time Connection');

  return new Promise((resolve, reject) => {
    const ws = new WebSocket(`ws://localhost:5000/api/ws`);
    let authenticated = false;

    const timeout = setTimeout(() => {
      console.log('‚ö†Ô∏è  WebSocket test timeout (10s)');
      ws.close();
      resolve(); // Don't reject, just resolve with warning
    }, 10000);

    ws.on('open', () => {
      console.log('‚úÖ WebSocket connected');
      // Send auth message
      ws.send(JSON.stringify({ type: 'auth', userId }));
    });

    ws.on('message', (data) => {
      const message = JSON.parse(data.toString());

      if (message.type === 'authenticated') {
        authenticated = true;
        console.log(`‚úÖ WebSocket authenticated for user ${userId}`);

        // Test typing indicator
        ws.send(JSON.stringify({ 
          type: 'typing_start', 
          conversationUserId: 25 // Example user
        }));
        console.log('‚úÖ Typing indicator sent');

        // Clean up after 2 seconds
        setTimeout(() => {
          clearTimeout(timeout);
          ws.close();
          console.log('‚úÖ WebSocket test completed');
          resolve();
        }, 2000);
      } else {
        console.log(`   WebSocket event: ${message.type}`);
      }
    });

    ws.on('error', (error) => {
      console.log(`‚ö†Ô∏è  WebSocket error: ${error.message}`);
      clearTimeout(timeout);
      resolve(); // Don't fail the test
    });

    ws.on('close', () => {
      if (!authenticated) {
        console.log('‚ö†Ô∏è  WebSocket closed before authentication');
      }
      clearTimeout(timeout);
      resolve();
    });
  });
}

// Run all tests
async function runAllTests() {
  console.log('‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê');
  console.log('  STUDIO LEFLOW - MESSAGING SYSTEM E2E TESTS');
  console.log('‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê');

  try {
    // Test 1: Authentication
    const currentUser = await testAuthentication();

    if (!currentUser.emailVerified) {
      console.log('\n‚ö†Ô∏è  WARNING: Current user email not verified!');
      console.log('   Messaging features require email verification.');
      console.log('   Some tests may fail due to requireVerifiedEmail middleware.');
    }

    // Test 2: User Search
    const users = await testUserSearch('v');

    // Test 3: Get Conversations
    await testGetConversations();

    // Test 4-7: Message Flow (if we have users to message)
    if (users.length > 0 && currentUser.emailVerified) {
      const targetUser = users.find((u: any) => u.id !== currentUser.id);

      if (targetUser) {
        // Send message
        const message = await testSendMessage(
          targetUser.id, 
          `Test message from automated E2E test - ${new Date().toISOString()}`
        );

        if (message) {
          // Get messages
          await testGetMessages(targetUser.id);

          // Mark as read
          await testMarkAsRead(targetUser.id);

          // Check unread count
          await testUnreadCount();
        }
      }
    } else if (!currentUser.emailVerified) {
      console.log('\n‚ö†Ô∏è  Skipping message flow tests (email not verified)');
    }

    // Test 8: WebSocket
    if (currentUser.emailVerified) {
      await testWebSocket(currentUser.id);
    } else {
      console.log('\n‚ö†Ô∏è  Skipping WebSocket test (email not verified)');
    }

    console.log('\n‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê');
    console.log('  ‚úÖ ALL TESTS COMPLETED');
    console.log('‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê\n');

  } catch (error: any) {
    console.error('\n‚ùå TEST SUITE FAILED:', error.message);
    console.error('Stack trace:', error.stack);
    process.exit(1);
  }
}

// Execute tests
runAllTests();
